mapper.select:
select TABLE_SCHEMA as tableSchema, TABLE_NAME as tableName, TABLE_COMMENT as tableComment, CREATE_TIME as createTime from information_schema.tables where table_schema=#{tableSchema}
and TABLE_SCHEMA in
#{tableName}

select TABLE_SCHEMA as tableSchema, TABLE_NAME as tableName, COLUMN_NAME as columnName, DATA_TYPE as dataType, COLUMN_COMMENT as columnComment, COLUMN_KEY as columnKey from information_schema.COLUMNS where TABLE_SCHEMA = #{tableSchema}
and TABLE_SCHEMA in
#{tableName}
order by ORDINAL_POSITION
